t = [0:0.01:0.98];y1 = sin(2*pi*4*t);y2 = cos(2*pi*4*t);plot(t, y1);plot(t, y2);hold on;plot(t, y2, 'r');xlabel('time')ylabel('value')legend('sin', 'cos')title('Plot')%cd './plots'; print -dpng 'plot.png'clf;close;subplot(1,2,1); %divides plot a 1x2 grid, access first elementplot(t,y1);subplot(1,2,2);plot(t,y1);axis([0.5 1 -1 1]);clf;close;A=magic(15);imagesc(A)imagesc(A), colorbar, colormap gray;